    top: 300px;
    margin: 0 30px;
    text-align: center;

}

@keyframes floatLeft {
    0% {
        transform: translateX(100%) translateY(50%); 
    }
    100% {
        transform: translateX(18%) translateY(20%);
    }
}

@keyframes hover {
    0% {
        transform: translateX(18%) translateY(20%);
    }
    100% {
        transform: translateX(18%) translateY(15%);
    }
}

@keyframes hoverSmall {
    0% {
        transform: rotate(-5deg);
    }
    100% {
        transform: rotate(5deg);
    }
}

@keyframes floatUp {
    0% {
        opacity: 0%;
        transform: translateY(100%) scale(95%); 
    }

    67% {

            opacity: 0%;
            transform: translateY(100%) scale(95%); 
    }
    100% {
        opacity: 100%;
        transform: translateY(25%) scale(100%);
    }
}

